import{_ as n,c as a,o as p,ag as l}from"./chunks/framework.BPQUDke-.js";const d=JSON.parse('{"title":"数字、字符串、列表及相关操作方法。","description":"","frontmatter":{"outline":[2,3,4]},"headers":[],"relativePath":"python/01.md","filePath":"python/01.md","lastUpdated":1740476554000}'),o={name:"python/01.md"};function e(t,s,r,c,i,E){return p(),a("div",null,s[0]||(s[0]=[l(`<h1 id="数字、字符串、列表及相关操作方法。" tabindex="-1">数字、字符串、列表及相关操作方法。 <a class="header-anchor" href="#数字、字符串、列表及相关操作方法。" aria-label="Permalink to &quot;数字、字符串、列表及相关操作方法。&quot;">​</a></h1><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><p>常规安装：<a href="https://www.python.org/downloads/macos/" target="_blank" rel="noreferrer">https://www.python.org/downloads/macos/</a></p><p>使用 <code>pyenv</code> 安装：<a href="https://github.com/pyenv/pyenv?tab=readme-ov-file#a-getting-pyenv" target="_blank" rel="noreferrer">https://github.com/pyenv/pyenv?tab=readme-ov-file#a-getting-pyenv</a></p><table tabindex="0"><thead><tr><th>常用指令</th><th>说明</th></tr></thead><tbody><tr><td><code>pyenv install -l</code></td><td>列出所有版本</td></tr><tr><td><code>pyenv versions</code></td><td>列出已经安装的版本</td></tr><tr><td><code>pyenv install 3.12.4</code></td><td>安装指定版本</td></tr><tr><td><code>pyenv global 3.12.4</code></td><td>设置为全局版本</td></tr><tr><td><code>pyenv local 3.12.4</code></td><td>设置为当前项目版本</td></tr><tr><td><code>pyenv uninstall 3.12.4</code></td><td>删除指定版本</td></tr></tbody></table><h2 id="常识" tabindex="-1">常识 <a class="header-anchor" href="#常识" aria-label="Permalink to &quot;常识&quot;">​</a></h2><ul><li><code>.py</code> 文件中的字符编码，默认为 <code>utf-8</code> 编码；如果需要修改可以在文件头部指定 <code># -*- coding: utf-8 -*-</code>；</li><li>如果需要在 <code>Linux</code> 中让 <code>.py</code> 文件像 <code>shell</code> 一样执行，则需要在文件头部添加 <code>#!/usr/bin/env python3</code> 来指定解释器 <strong>注意同时出现时，此条优先级更高</strong>；</li><li><code>#</code> 表示注释，<code>#</code> 后的内容不会被解释器执行。 <code># print(&#39;hello&#39;)</code></li><li><code>&quot;&quot;&quot;</code> 或者 <code>&#39;&#39;&#39;</code> 表示多行字符串，<code>&quot;&quot;&quot;...&quot;&quot;&quot;</code> 或者 <code>&#39;&#39;&#39;...&quot;&quot;&quot;</code> 可以包含多行内容，<code>&quot;&quot;&quot;...&quot;&quot;&quot;</code> 和 <code>&#39;&#39;&#39;...&quot;&quot;&quot;</code> 可以互相嵌套。</li></ul><h2 id="数字-int-float" tabindex="-1">数字 int, float <a class="header-anchor" href="#数字-int-float" aria-label="Permalink to &quot;数字 int, float&quot;">​</a></h2><p>数字之间可以直接进行运算，表达式语法很直观：运算符 <code>+</code>, <code>-</code>, <code>*</code> 和 <code>/</code> 以及 <code>//</code>, <code>**</code>, <code>%</code> 可被用来执行算术运算；圆括号 <code>()</code> 可被用来进行分组。</p><p><strong>需要注意混合运算和整除运算的结果始终是 <code>float</code> 类型</strong></p><div class="language-python line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki slack-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A9955;"># 基础运算</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#B5CEA8;">2</span><span style="color:#D4D4D4;"> +</span><span style="color:#B5CEA8;"> 2</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># 4</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#B5CEA8;">50</span><span style="color:#D4D4D4;"> -</span><span style="color:#B5CEA8;"> 5</span><span style="color:#D4D4D4;"> *</span><span style="color:#B5CEA8;"> 6</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># 20</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">((</span><span style="color:#B5CEA8;">50</span><span style="color:#D4D4D4;"> -</span><span style="color:#B5CEA8;"> 5</span><span style="color:#E6E6E6;">) </span><span style="color:#D4D4D4;">*</span><span style="color:#B5CEA8;"> 6</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># 270</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#B5CEA8;">8</span><span style="color:#D4D4D4;"> /</span><span style="color:#B5CEA8;"> 5</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># 1.6</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A9955;"># 整除运算</span><span style="color:#6A9955;"> // 会向下取整</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#B5CEA8;">17</span><span style="color:#D4D4D4;"> /</span><span style="color:#B5CEA8;"> 3</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># 5.666666666666667</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#B5CEA8;">17</span><span style="color:#D4D4D4;"> //</span><span style="color:#B5CEA8;"> 3</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># 5</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A9955;"># 取余运算 %</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#B5CEA8;">17</span><span style="color:#D4D4D4;"> %</span><span style="color:#B5CEA8;"> 3</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># 2</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A9955;"># 乘方运算 **</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#B5CEA8;">5</span><span style="color:#D4D4D4;"> **</span><span style="color:#B5CEA8;"> 2</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># 25</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#B5CEA8;">2</span><span style="color:#D4D4D4;"> **</span><span style="color:#B5CEA8;"> 7</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># 128</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A9955;"># 变量赋值</span></span>
<span class="line"><span style="color:#E6E6E6;">width </span><span style="color:#D4D4D4;">=</span><span style="color:#B5CEA8;"> 20</span></span>
<span class="line"><span style="color:#E6E6E6;">height </span><span style="color:#D4D4D4;">=</span><span style="color:#B5CEA8;"> 5</span><span style="color:#D4D4D4;"> *</span><span style="color:#B5CEA8;"> 9</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(width </span><span style="color:#D4D4D4;">*</span><span style="color:#E6E6E6;"> height)  </span><span style="color:#6A9955;"># 900</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br></div></div><p>除了 <code>int</code> 和 <code>float</code>，Python 还支持其他数字类型，例如 <code>Decimal</code> 或 <code>Fraction</code>。Python 还内置支持 复数，后缀 <code>j</code> 或 <code>J</code> 用于表示虚数（例如 <code>3+5j</code> ）。</p><h2 id="字符串-str" tabindex="-1">字符串 str <a class="header-anchor" href="#字符串-str" aria-label="Permalink to &quot;字符串 str&quot;">​</a></h2><p>被 <code>&quot;&quot;</code> 或 <code>&#39;&#39;</code> 包裹的内容被视为字符串。如果要表示引号本身，则需要使用转义字符 <code>\\</code>。此外还有一些常见的转义字符：</p><table tabindex="0"><thead><tr><th>转义字符</th><th>说明</th></tr></thead><tbody><tr><td><code>\\n</code></td><td>换行</td></tr><tr><td><code>\\t</code></td><td>制表符</td></tr><tr><td><code>\\\\</code></td><td>反斜杠</td></tr></tbody></table><p>被 <code>r</code> 包裹的字符串被视为原始字符串，其中的转义字符不会被解释为转义字符，而是被视为普通字符。</p><div class="language-python line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki slack-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A9955;"># 单引号和双引号</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#CE9178;">&#39;spam eggs&#39;</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># spam eggs</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#CE9178;">&#39;doesn</span><span style="color:#D7BA7D;">\\&#39;</span><span style="color:#CE9178;">t&#39;</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># doesn&#39;t</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#CE9178;">&quot;doesn&#39;t&quot;</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># doesn&#39;t</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#CE9178;">&#39;&quot;Yes,&quot; they said.&#39;</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># &quot;Yes,&quot; they said.</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#CE9178;">&quot;</span><span style="color:#D7BA7D;">\\&quot;</span><span style="color:#CE9178;">Yes,</span><span style="color:#D7BA7D;">\\&quot;</span><span style="color:#CE9178;"> they said.&quot;</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># &quot;Yes,&quot; they said.</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#CE9178;">&#39;&quot;Isn</span><span style="color:#D7BA7D;">\\&#39;</span><span style="color:#CE9178;">t,&quot; they said.&#39;</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># &quot;Isn&#39;t,&quot; they said.</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A9955;"># 转义字符</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#CE9178;">&#39;C:\\some</span><span style="color:#D7BA7D;">\\n</span><span style="color:#CE9178;">ame&#39;</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># C:\\some\\name</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#CE9178;">&#39;C:</span><span style="color:#D7BA7D;">\\\\</span><span style="color:#CE9178;">some</span><span style="color:#D7BA7D;">\\\\</span><span style="color:#CE9178;">name&#39;</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># C:\\some\\name</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#569CD6;">r</span><span style="color:#D16969;">&#39;C:\\some</span><span style="color:#D7BA7D;">\\n</span><span style="color:#D16969;">ame&#39;</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># C:\\some\\name (raw string)</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br></div></div><p>同时，字符串可以被 <code>+</code> 拼接，也可以被 <code>*</code> 重复。也可以多行书写。</p><div class="language-python line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki slack-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#CE9178;">&quot;&quot;&quot;</span><span style="color:#569CD6;">\\</span></span>
<span class="line"><span style="color:#CE9178;">Usage: thingy [OPTIONS]</span></span>
<span class="line"><span style="color:#CE9178;">     -h                        Display this usage message</span></span>
<span class="line"><span style="color:#CE9178;">     -H hostname               Hostname to connect to</span></span>
<span class="line"><span style="color:#CE9178;">&quot;&quot;&quot;</span><span style="color:#E6E6E6;">)</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#B5CEA8;">3</span><span style="color:#D4D4D4;"> *</span><span style="color:#CE9178;"> &#39;un&#39;</span><span style="color:#D4D4D4;"> +</span><span style="color:#CE9178;"> &#39;ium&#39;</span><span style="color:#E6E6E6;">)  </span><span style="color:#6A9955;"># unununium</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><p>字符串也可以被索引，索引从 0 开始，-1 表示最后一个元素，-2 表示倒数第二个元素，以此类推。</p><div class="language-python line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki slack-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#CE9178;">&quot;Hello, World!&quot;</span><span style="color:#E6E6E6;">[</span><span style="color:#B5CEA8;">0</span><span style="color:#E6E6E6;">])  </span><span style="color:#6A9955;"># H</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#CE9178;">&quot;Hello, World!&quot;</span><span style="color:#E6E6E6;">[</span><span style="color:#D4D4D4;">-</span><span style="color:#B5CEA8;">1</span><span style="color:#E6E6E6;">])  </span><span style="color:#6A9955;"># !</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h3 id="str-的方法" tabindex="-1">str 的方法 <a class="header-anchor" href="#str-的方法" aria-label="Permalink to &quot;str 的方法&quot;">​</a></h3><table tabindex="0"><thead><tr><th>方法</th><th>参数</th><th>说明</th></tr></thead><tbody><tr><td><code>capitalize()</code></td><td>无</td><td>将字符串首字母大写,其余字母小写</td></tr><tr><td><code>upper()</code></td><td>无</td><td>将字符串全部转为大写</td></tr><tr><td><code>lower()</code></td><td>无</td><td>将字符串全部转为小写</td></tr><tr><td><code>strip()</code></td><td>[chars]</td><td>移除字符串两端的空白字符或指定字符</td></tr><tr><td><code>split()</code></td><td>[sep[, maxsplit]]</td><td>按分隔符分割字符串为列表,可指定分割次数</td></tr><tr><td><code>join()</code></td><td>iterable</td><td>用字符串连接可迭代对象中的元素</td></tr><tr><td><code>replace()</code></td><td>old, new[, count]</td><td>替换字符串中的子串,可指定替换次数</td></tr><tr><td><code>find()</code></td><td>sub[, start[, end]]</td><td>查找子串位置,返回索引,未找到返回-1</td></tr><tr><td><code>startswith()</code></td><td>prefix[, start[, end]]</td><td>判断字符串是否以指定前缀开头</td></tr><tr><td><code>endswith()</code></td><td>suffix[, start[, end]]</td><td>判断字符串是否以指定后缀结尾</td></tr></tbody></table><div class="language-python line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki slack-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#E6E6E6;">s </span><span style="color:#D4D4D4;">=</span><span style="color:#CE9178;"> &quot;  Hello, World!  &quot;</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(s.capitalize())  </span><span style="color:#6A9955;"># Hello, world!</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(s.upper())      </span><span style="color:#6A9955;"># HELLO, WORLD!</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(s.lower())      </span><span style="color:#6A9955;"># hello, world!</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(s.strip())      </span><span style="color:#6A9955;"># Hello, World!</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A9955;"># split 和 join</span></span>
<span class="line"><span style="color:#E6E6E6;">words </span><span style="color:#D4D4D4;">=</span><span style="color:#CE9178;"> &quot;python is awesome&quot;</span><span style="color:#E6E6E6;">.split()  </span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(words)  </span><span style="color:#6A9955;"># [&#39;python&#39;, &#39;is&#39;, &#39;awesome&#39;]</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(</span><span style="color:#CE9178;">&quot;-&quot;</span><span style="color:#E6E6E6;">.join(words))  </span><span style="color:#6A9955;"># python-is-awesome</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A9955;"># replace</span></span>
<span class="line"><span style="color:#E6E6E6;">text </span><span style="color:#D4D4D4;">=</span><span style="color:#CE9178;"> &quot;I love python, python is great&quot;</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(text.replace(</span><span style="color:#CE9178;">&quot;python&quot;</span><span style="color:#E6E6E6;">, </span><span style="color:#CE9178;">&quot;JavaScript&quot;</span><span style="color:#E6E6E6;">, </span><span style="color:#B5CEA8;">1</span><span style="color:#E6E6E6;">))  </span><span style="color:#6A9955;"># I love JavaScript, python is great</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A9955;"># find</span></span>
<span class="line"><span style="color:#E6E6E6;">sentence </span><span style="color:#D4D4D4;">=</span><span style="color:#CE9178;"> &quot;Python is easy to learn&quot;</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(sentence.find(</span><span style="color:#CE9178;">&quot;easy&quot;</span><span style="color:#E6E6E6;">))  </span><span style="color:#6A9955;"># 10</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(sentence.find(</span><span style="color:#CE9178;">&quot;hard&quot;</span><span style="color:#E6E6E6;">))  </span><span style="color:#6A9955;"># -1</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A9955;"># startswith 和 endswith</span></span>
<span class="line"><span style="color:#E6E6E6;">filename </span><span style="color:#D4D4D4;">=</span><span style="color:#CE9178;"> &quot;document.txt&quot;</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(filename.startswith(</span><span style="color:#CE9178;">&quot;doc&quot;</span><span style="color:#E6E6E6;">))  </span><span style="color:#6A9955;"># True</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(filename.endswith(</span><span style="color:#CE9178;">&quot;.txt&quot;</span><span style="color:#E6E6E6;">))   </span><span style="color:#6A9955;"># True</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br></div></div><h2 id="列表-list" tabindex="-1">列表 list <a class="header-anchor" href="#列表-list" aria-label="Permalink to &quot;列表 list&quot;">​</a></h2><p>支持多种数据类型，支持嵌套。用方括号标注，逗号分隔的一组值。列表中的元素可以被修改。支持下标索引和拼接。</p><p><strong>注意：列表的索引从 0 开始，-1 表示最后一个元素，-2 表示倒数第二个元素，以此类推。</strong></p><div class="language-python line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki slack-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A9955;"># 创建列表</span></span>
<span class="line"><span style="color:#E6E6E6;">fruits </span><span style="color:#D4D4D4;">=</span><span style="color:#E6E6E6;"> [</span><span style="color:#CE9178;">&quot;apple&quot;</span><span style="color:#E6E6E6;">, </span><span style="color:#CE9178;">&quot;banana&quot;</span><span style="color:#E6E6E6;">, </span><span style="color:#CE9178;">&quot;cherry&quot;</span><span style="color:#E6E6E6;">]</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A9955;"># 下标索引</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(fruits[</span><span style="color:#B5CEA8;">0</span><span style="color:#E6E6E6;">])  </span><span style="color:#6A9955;"># apple</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A9955;"># 拼接</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(fruits </span><span style="color:#D4D4D4;">+</span><span style="color:#E6E6E6;"> [</span><span style="color:#CE9178;">&quot;orange&quot;</span><span style="color:#E6E6E6;">, </span><span style="color:#CE9178;">&quot;pear&quot;</span><span style="color:#E6E6E6;">])  </span><span style="color:#6A9955;"># [&#39;apple&#39;, &#39;banana&#39;, &#39;cherry&#39;, &#39;orange&#39;, &#39;pear&#39;]</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><h2 id="str、list-的切片" tabindex="-1">str、list 的切片 <a class="header-anchor" href="#str、list-的切片" aria-label="Permalink to &quot;str、list 的切片&quot;">​</a></h2><p>切片接受三个参数：起始索引、结束索引和步长。返回一个新的列表或字符串，包含从起始索引到结束索引之间的元素。</p><p>如果省略起始索引，则从第一个元素开始；如果省略结束索引，则到列表末尾结束。</p><p><strong>步长为正数时，从左到右切片；步长为负数时，从右到左切片。</strong></p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki slack-dark vp-code" tabindex="0"><code><span class="line"><span> +---+---+---+---+---+---+</span></span>
<span class="line"><span> | P | y | t | h | o | n |</span></span>
<span class="line"><span> +---+---+---+---+---+---+</span></span>
<span class="line"><span> 0   1   2   3   4   5   6</span></span>
<span class="line"><span>-6  -5  -4  -3  -2  -1</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><div class="language-python line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki slack-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A9955;"># 字符串切片</span></span>
<span class="line"><span style="color:#E6E6E6;">text </span><span style="color:#D4D4D4;">=</span><span style="color:#CE9178;"> &quot;Hello, World!&quot;</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(text[</span><span style="color:#B5CEA8;">0</span><span style="color:#E6E6E6;">:</span><span style="color:#B5CEA8;">5</span><span style="color:#E6E6E6;">])    </span><span style="color:#6A9955;"># Hello</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(text[:</span><span style="color:#B5CEA8;">5</span><span style="color:#E6E6E6;">])     </span><span style="color:#6A9955;"># Hello</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(text[</span><span style="color:#B5CEA8;">7</span><span style="color:#E6E6E6;">:])     </span><span style="color:#6A9955;"># World!</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(text[::</span><span style="color:#B5CEA8;">2</span><span style="color:#E6E6E6;">])    </span><span style="color:#6A9955;"># Hlo ol!</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(text[::</span><span style="color:#D4D4D4;">-</span><span style="color:#B5CEA8;">1</span><span style="color:#E6E6E6;">])   </span><span style="color:#6A9955;"># !dlroW ,olleH</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A9955;"># 列表切片 </span></span>
<span class="line"><span style="color:#E6E6E6;">numbers </span><span style="color:#D4D4D4;">=</span><span style="color:#E6E6E6;"> [</span><span style="color:#B5CEA8;">0</span><span style="color:#E6E6E6;">, </span><span style="color:#B5CEA8;">1</span><span style="color:#E6E6E6;">, </span><span style="color:#B5CEA8;">2</span><span style="color:#E6E6E6;">, </span><span style="color:#B5CEA8;">3</span><span style="color:#E6E6E6;">, </span><span style="color:#B5CEA8;">4</span><span style="color:#E6E6E6;">, </span><span style="color:#B5CEA8;">5</span><span style="color:#E6E6E6;">]</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(numbers[</span><span style="color:#B5CEA8;">2</span><span style="color:#E6E6E6;">:</span><span style="color:#B5CEA8;">4</span><span style="color:#E6E6E6;">])     </span><span style="color:#6A9955;"># [2, 3]</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(numbers[:</span><span style="color:#B5CEA8;">3</span><span style="color:#E6E6E6;">])      </span><span style="color:#6A9955;"># [0, 1, 2]</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(numbers[</span><span style="color:#B5CEA8;">3</span><span style="color:#E6E6E6;">:])      </span><span style="color:#6A9955;"># [3, 4, 5]</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(numbers[::</span><span style="color:#B5CEA8;">2</span><span style="color:#E6E6E6;">])     </span><span style="color:#6A9955;"># [0, 2, 4]</span></span>
<span class="line"><span style="color:#DCDCAA;">print</span><span style="color:#E6E6E6;">(numbers[::</span><span style="color:#D4D4D4;">-</span><span style="color:#B5CEA8;">1</span><span style="color:#E6E6E6;">])    </span><span style="color:#6A9955;"># [5, 4, 3, 2, 1, 0]</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br></div></div>`,34)]))}const u=n(o,[["render",e]]);export{d as __pageData,u as default};
